var dt = {
  depart: null,
  return: null
};
_hotelSearch.default.tfDepartDate().invoke('val').then(d => dt.depart = Date.parse(d));
_hotelSearch.default.tfReturnDate().invoke('val').then(d => dt.return = Date.parse(d));
_hotelSearch.default.elStartDate().then(e => {
  cy.wrap(e).find('a').should('have.css', 'background-color', 'rgb(0, 93, 186)').invoke('text').then(day => Date.parse(`${e.attr('data-year')}-${Number(e.attr('data-month')) + 1}-${day}`)).should('eq', dt.depart);
});
_hotelSearch.default.elEndDate().then(e => {
  cy.wrap(e).find('a').should('have.css', 'background-color', 'rgb(0, 93, 186)').invoke('text').then(day => Date.parse(`${e.attr('data-year')}-${Number(e.attr('data-month')) + 1}-${day}`)).should('eq', dt.return);
});
_hotelSearch.default.elBetweenDate().each(e => {
  cy.wrap(e).find('a').should('have.css', 'background-color', 'rgba(0, 0, 0, 0)').invoke('text').then(day => Date.parse(`${e.attr('data-year')}-${Number(e.attr('data-month')) + 1}-${day}`)).should('be.above', dt.depart).and('be.below', dt.return);
});

